func TestA(t *testing.T) {

	if f, err := os.OpenFile("test.txt", os.O_RDWR, 0555); err == nil {

		conn := NewReadWriteCloserConnection(f)
		conn.Write([]byte(""))
		conn.Write([]byte(""))
		conn.Write([]byte(""))
		f.Close()
	} else {
		panic(f)
	}

}
